-- ----------------------------
-- Table structure for blog_info
-- ----------------------------
DROP TABLE IF EXISTS `blog_info`;
CREATE TABLE `blog_info` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(225) DEFAULT NULL,
  `blog_markdown` text,
  `imag` varchar(225) DEFAULT NULL,
  `audio` varchar(225) DEFAULT NULL,
  `blog_type` int(11) DEFAULT NULL,
  `upload_time` datetime DEFAULT NULL,
  `blog_timeid` varchar(225) DEFAULT NULL,
  `user_id` varchar(225) DEFAULT NULL,
  `view` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `id` (`id`),
  KEY `user_id` (`user_id`),
  CONSTRAINT `blog_info_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `user_info` (`uid`)
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

