-- ----------------------------
-- Table structure for pictures
-- ----------------------------
DROP TABLE IF EXISTS `pictures`;
CREATE TABLE `pictures` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(225) DEFAULT NULL,
  `text` varchar(225) DEFAULT NULL,
  `ad` varchar(225) DEFAULT NULL,
  `imag` varchar(225) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `id` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=21 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

-- ----------------------------
-- Records of pictures
-- ----------------------------
BEGIN;
INSERT INTO `pictures` VALUES (1, '天野远子', '“有时必须破坏原有的一切，受尽伤害，才有办法了解某些事，见到某些风景，体会到某些心情。”', '追想画廊', 'images/thumbs/1.jpg');
INSERT INTO `pictures` VALUES (2, '天野远子', '“你说我是妖怪，这是歧视用语，我只是个想将这世上的故事或文学全部吞食进去，对文学有着深切且激烈爱意的普通高中生，一个平凡的文学少女。”', '追想画廊', 'images/thumbs/2.jpg');
INSERT INTO `pictures` VALUES (3, '雨宫茧', '“嘿，心叶，你知道怎样才能拿回失去的东西吗？只要让时间倒流就好了。”', '追想画廊', 'images/thumbs/3.jpg');
INSERT INTO `pictures` VALUES (4, '竹田千爱', '“掩饰内心的演技没什么了不起，大家都办得到。”', '追想画廊', 'images/thumbs/4.jpg');
INSERT INTO `pictures` VALUES (5, '天野远子', '“是啊，可是我并不是心叶的妄想，我就真实地站在这里。”', '追想画廊', 'images/thumbs/5.jpg');
INSERT INTO `pictures` VALUES (6, '天野远子', '“只要是人，大家都是愚者。”', '追想画廊', 'images/thumbs/6.jpg');
INSERT INTO `pictures` VALUES (7, '水户夕歌', '“不能哭！唱歌吧！继续唱下去吧！ <br>不是为了赞美神，是为了向神挑战而唱！”', '追想画廊', 'images/thumbs/7.jpg');
INSERT INTO `pictures` VALUES (8, '朝仓美羽', '“如果你一直当我养的狗不就好了吗？”', '追想画廊', 'images/thumbs/8.jpg');
INSERT INTO `pictures` VALUES (9, '井上心叶', '“请告诉我，只要是你的希望，我什么都愿意做。我的手、脚、眼睛，什么是生命，什么都可以给你。”', '追想画廊', 'images/thumbs/9.jpg');
INSERT INTO `pictures` VALUES (10, '白雪', '“人命与我何干！若为爱情，我此身尽灭也在所不惜！”', '追想画廊', 'images/thumbs/10.jpg');
INSERT INTO `pictures` VALUES (11, '天野远子', '“映在镜里的花朵，浮在水上的月亮，都是看得见而摸不着，如果想要抓住就会化为虚无。但也正是如此，只要我们不遗忘，就能在心中永远留下那片美景。”', '追想画廊', 'images/thumbs/11.jpg');
INSERT INTO `pictures` VALUES (12, '井上心叶', '“今后的我就像小丑一样，藏起哀伤而笑吧。即使有时候像幽灵般饥渴，有时想愚者一样误下判断，有时像堕落天使那样沾染污秽，我也要怀抱着花与月，像个行往圣地的信徒一般继续走下去。然后，就这样成为迈向神境的作家吧。成为能够看见真实，并且为它点亮名为想象的灯光，籍此创造出新世界的作家。”', '追想画廊', 'images/thumbs/12.jpg');
INSERT INTO `pictures` VALUES (13, '井上心叶', '“如果心中满载过去得到的种种事物，那么，就算走近狭窄黑暗的道路也不会感到害怕，因为想象的力量可以照亮阴暗的道路。”', '追想画廊', 'images/thumbs/13.jpg');
INSERT INTO `pictures` VALUES (14, '天野远子', '“人就是切断了母亲和自己的联系，才能诞生在这个世上。如果不试着斩断过去，就无法迈向未来！”', '追想画廊', 'images/thumbs/14.jpg');
INSERT INTO `pictures` VALUES (15, '井上心叶', '“永远维持的关系只会出现在天真的童话里，如果轻易相信这种东西又遭到背叛，只会让人受到伤害。”', '追想画廊', 'images/thumbs/15.jpg');
INSERT INTO `pictures` VALUES (16, '天野远子', '“一个故事的阅读方法，绝对不只有一种。只要还有别的登场人物，就会有别的故事存在。”', '追想画廊', 'images/thumbs/16.jpg');
INSERT INTO `pictures` VALUES (17, '反町亮太', '“尽管在今后，我有时会变得懦弱、没用、原地踏步不前、因心爱的女孩的一句话而兴奋得忘乎所以。但我们的生活会充满诗意。”', '追想画廊', 'images/thumbs/17.jpg');
INSERT INTO `pictures` VALUES (18, '朝仓美羽', '“在寒冷的房间里，看着天空渐渐发白发亮、太阳渐渐升起的时候，我仿佛受到制裁、受到惩罚，身体好像要被那照亮一切的光芒割裂了。”', '追想画廊', 'images/thumbs/18.jpg');
INSERT INTO `pictures` VALUES (19, '竹田千爱', '“既然有善良的人，也就会有坏心人，更有对什么都漠不关心的人。不管是学校或社会，一定都是这样。”', '追想画廊', 'images/thumbs/19.jpg');
INSERT INTO `pictures` VALUES (20, '万叶集', '“恋ひ恋ひて逢へる时だに爱しき言尽くしてよ长く思はば。 <br>就算现在正看着你的时候，也好想要你继续那些温柔的话语，但愿让我们两的关系长久的继续下去。”', '追想画廊', 'images/thumbs/20.jpg');
COMMIT;

-- ----------------------------
-- Table structure for user_info
-- ----------------------------
DROP TABLE IF EXISTS `user_info`;
CREATE TABLE `user_info` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` varchar(225) DEFAULT NULL,
  `password` varchar(225) DEFAULT NULL,
  `name` varchar(225) DEFAULT NULL,
  `email` varchar(225) DEFAULT NULL,
  `sign` longtext,
  `icon` varchar(225) DEFAULT NULL,
  `register_login` datetime DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `id` (`id`),
  UNIQUE KEY `uid` (`uid`)
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

